// Normalize incoming payload to a flat row
const item = items[0].json;
const priceNumber = (() => {
  const p = String(item.price||'').replace(/[^0-9.,]/g,'').replace(',', '.');
  const m = p.match(/([0-9]+(?:\.[0-9]+)?)/);
  return m ? parseFloat(m[1]) : null;
})();
return [{ json: {
  url: item.url || '',
  title: item.title || '',
  price: priceNumber,
  images: (item.images||[]).join(' | '),
  specs: JSON.stringify(item.specs||[])
}}];
